module Particle Species

abstract type ParticleSpecies end; export ParticleSpecies

mutable struct FundamentalSpecies <: ParticleSpecies
  name::String
end

mutable struct AtomSpecies <: ParticleSpecies
  name:: String
  nucleons::Int32   # Zero => Use isotopic average.
  charge::Float64
end; export AtomSpecies

mutable struct MoleculeSpecies <: ParticleSpecies
  name:: String
  charge::Float64
end; export MoleculeSpecies

